package com.senierr.mediaimport android.Manifestimport android.content.Intentimport android.os.Bundleimport android.view.LayoutInflaterimport androidx.activity.result.contract.ActivityResultContractsimport com.pateo.module.usb.domain.UsbMainActivityimport com.senierr.base.support.ktx.onClickimport com.senierr.base.support.ui.BaseActivityimport com.senierr.media.databinding.ActivityMainBinding/** * 首页 * * @author senierr_zhou * @date 2023/07/28 */class MainActivity : BaseActivity<ActivityMainBinding>() {    override fun createViewBinding(layoutInflater: LayoutInflater): ActivityMainBinding {        return ActivityMainBinding.inflate(layoutInflater)    }    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        val permissionLauncher = registerForActivityResult(            ActivityResultContracts.RequestMultiplePermissions()        ) { permissions ->            // 所有权限通过            if (permissions.values.none { !it }) {            }        }        permissionLauncher.launch(arrayOf(            Manifest.permission.ACCESS_COARSE_LOCATION,            Manifest.permission.ACCESS_FINE_LOCATION        ))        initView()    }    private fun initView() {        binding.btnJumpPic.onClick {            startActivity(Intent(this, UsbMainActivity::class.java))        }    }}